mapper.update:
update item_details
set isTakeoff=0
where pid=#{pid}

mapper.select:
update community
set community_name=#{community_name},type_id=#{type_id}
where community_id=#{community_id};
select * from community where community_id=#{community_id}

mapper.update:
update user_role
set role_id=#{role_id}
where uid=#{uid}
